async function setStatus(status: WsStatus, reason?: string): Promise<void> {
  wsStatus = status;
  wsReason = reason;
  await broadcast({
    type: 'WS_STATUS_CHANGED',
    payload: { status, reason }
  });
}

function messageQueueKey(threadId: string, messageId: string): string {
  return `${threadId}:${messageId}`;
}

function enqueueMessageUpdate(
  threadId: string,
  messageId: string,
  task: () => Promise<void>
): Promise<void> {
  const key = messageQueueKey(threadId, messageId);
  const prev = messageUpdateQueue.get(key) ?? Promise.resolve();
  const next = prev
    .catch(() => {
      // previous task error は後続更新を止めない
    })
    .then(task);

  messageUpdateQueue.set(key, next);
  return next.finally(() => {
    if (messageUpdateQueue.get(key) === next) {
      messageUpdateQueue.delete(key);
    }
  });
}

