import type { Attachment, WsStatus } from '../contracts/types';
import { computeBackoffMs } from '../shared/backoff';

interface ChatSendPayload {
  threadId: string;
  messageId: string;
  text: string;
  attachments: Attachment[];
}

interface TransportEvents {
  onStatus: (status: WsStatus, reason?: string) => void;
  onToken: (event: { threadId: string; messageId: string; token: string }) => void;
  onDone: (event: { threadId: string; messageId: string }) => void;
  onError: (event: { threadId: string; messageId: string; error: string }) => void;
}

function extractString(input: unknown): string | undefined {
  return typeof input === 'string' ? input : undefined;
}

function parseIncoming(raw: string):
  | { kind: 'token'; threadId: string; messageId: string; token: string }
  | { kind: 'done'; threadId: string; messageId: string }
  | { kind: 'error'; threadId: string; messageId: string; error: string }
  | { kind: 'unknown' } {
  try {
    const data = JSON.parse(raw) as Record<string, unknown>;
    const type = extractString(data.type) ?? extractString(data.event);
    const threadId = extractString(data.threadId) ?? extractString(data.thread_id);
    const messageId = extractString(data.messageId) ?? extractString(data.message_id);

    if (!type || !threadId || !messageId) {
      return { kind: 'unknown' };
    }

    if (type.includes('token') || type.includes('delta')) {
      const token =
        extractString(data.token) ??
        extractString(data.delta) ??
        extractString((data.payload as Record<string, unknown> | undefined)?.token) ??
        '';

      return {
        kind: 'token',
        threadId,
        messageId,
        token
      };
    }

    if (type.includes('done') || type.includes('complete')) {
      return {
        kind: 'done',
        threadId,
        messageId
      };
    }

    if (type.includes('error')) {
      return {
        kind: 'error',
        threadId,
        messageId,
        error: extractString(data.error) ?? 'unknown error'
      };
    }

    return { kind: 'unknown' };
  } catch {
    return { kind: 'unknown' };
  }
}

export class WebSocketTransport {
  private socket: WebSocket | null = null;
  private reconnectTimer: number | null = null;
  private reconnectAttempts = 0;
  private shouldReconnect = true;
  private url = '';

  constructor(private readonly events: TransportEvents) {}

  connect(url: string): void {
    this.url = url;
    this.shouldReconnect = true;
    this.clearReconnectTimer();

    if (this.socket && this.socket.readyState === WebSocket.OPEN) {
      return;
    }

    this.events.onStatus('connecting');
    this.socket = new WebSocket(url);

    this.socket.onopen = () => {
      this.reconnectAttempts = 0;
      this.events.onStatus('connected');
    };

    this.socket.onmessage = (event) => {
      const parsed = parseIncoming(String(event.data));
      if (parsed.kind === 'token') {
        this.events.onToken(parsed);
      } else if (parsed.kind === 'done') {
        this.events.onDone(parsed);
      } else if (parsed.kind === 'error') {
        this.events.onError(parsed);
      }
    };

    this.socket.onerror = () => {
      this.events.onStatus('error', 'socket error');
    };

    this.socket.onclose = () => {
      this.events.onStatus('disconnected');
      this.socket = null;
      if (this.shouldReconnect) {
        this.scheduleReconnect();
      }
    };
  }

  disconnect(): void {
    this.shouldReconnect = false;
    this.clearReconnectTimer();
    if (this.socket) {
      this.socket.close();
      this.socket = null;
    }
    this.events.onStatus('disconnected', 'manual disconnect');
  }

  reconnectNow(): void {
    this.shouldReconnect = true;
    this.reconnectAttempts = 0;
    this.clearReconnectTimer();
    if (this.socket) {
      this.socket.close();
      this.socket = null;
    }
    if (this.url) {
      this.connect(this.url);
    }
  }

  sendChat(payload: ChatSendPayload): void {
    if (!this.socket || this.socket.readyState !== WebSocket.OPEN) {
      throw new Error('WebSocket is not connected');
    }

    this.socket.send(
      JSON.stringify({
        type: 'chat.send',
        ...payload
      })
    );
  }

  private scheduleReconnect(): void {
    this.clearReconnectTimer();
    const delay = computeBackoffMs(this.reconnectAttempts);
    this.reconnectAttempts += 1;

    this.reconnectTimer = globalThis.setTimeout(() => {
      if (this.url && this.shouldReconnect) {
        this.connect(this.url);
      }
    }, delay);
  }

  private clearReconnectTimer(): void {
    if (this.reconnectTimer !== null) {
      clearTimeout(this.reconnectTimer);
      this.reconnectTimer = null;
    }
  }
}
EOF"}

{"cmd":"cat > src/background.ts << 'EOF'
import type {
  ApiResponse,
  AttachSelectionResult,
  CreateThreadResult,
  ListThreadsResult,
  MessagesResult,
  RuntimeCommand,
  SettingsResult,
  SidePanelEvent
} from './contracts/messages';
import type { Attachment, Message, Thread, WsStatus } from './contracts/types';
import { StorageRepository } from './storage/repository';
import { DEFAULT_WS_URL } from './shared/constants';
import { createId } from './shared/id';
import { mergeToken } from './shared/token';
import { WebSocketTransport } from './transport/wsTransport';

const repository = new StorageRepository();

let wsStatus: WsStatus = 'disconnected';
let wsReason: string | undefined;

function now(): number {
  return Date.now();
}

function makeThreadTitle(): string {
  return `スレッド ${new Date().toLocaleString('ja-JP')}`;
}

async function broadcast(event: SidePanelEvent): Promise<void> {
  try {
    await chrome.runtime.sendMessage(event);
  } catch {
    // no-op: sidepanel/options が開いていないケースを許容
  }
}

async function setStatus(status: WsStatus, reason?: string): Promise<void> {
  wsStatus = status;
  wsReason = reason;
  await broadcast({
    type: 'WS_STATUS_CHANGED',
    payload: { status, reason }
  });
}

async function findMessage(threadId: string, messageId: string): Promise<Message | undefined> {
  const messages = await repository.getThreadMessages(threadId);
  return messages.find((msg) => msg.id === messageId);
}

const transport = new WebSocketTransport({
  onStatus: (status, reason) => {
    void setStatus(status, reason);
  },
  onToken: ({ threadId, messageId, token }) => {
    void (async () => {
      const current = await findMessage(threadId, messageId);
      const nextContent = mergeToken(current?.contentMd, token);
      await repository.updateMessage(threadId, messageId, {
        contentMd: nextContent,
        status: 'streaming'
      });

      await broadcast({
        type: 'CHAT_TOKEN',
        payload: { threadId, messageId, token }
      });
    })();
  },
  onDone: ({ threadId, messageId }) => {
    void (async () => {
      await repository.updateMessage(threadId, messageId, { status: 'done' });
      await broadcast({
        type: 'CHAT_DONE',
        payload: { threadId, messageId }
      });
    })();
  },
  onError: ({ threadId, messageId, error }) => {
    void (async () => {
      await repository.updateMessage(threadId, messageId, { status: 'error' });
      await broadcast({
        type: 'CHAT_ERROR',
        payload: { threadId, messageId, error }
      });
    })();
  }
});

async function ensureThread(threadId?: string): Promise<Thread> {
  if (threadId) {
    const threads = await repository.listThreads();
    const found = threads.find((item) => item.id === threadId);
    if (found) {
      return found;
    }
  }

  const createdAt = now();
  const thread: Thread = {
    id: createId('thread'),
    title: makeThreadTitle(),
    createdAt,
    updatedAt: createdAt,
    lastMessageAt: createdAt
  };
  await repository.upsertThread(thread);
  await repository.setCurrentThread(thread.id);
  return thread;
}

async function getActiveTab(tabId?: number): Promise<chrome.tabs.Tab> {
  if (typeof tabId === 'number') {
    const tab = await chrome.tabs.get(tabId);
    return tab;
  }

  const tabs = await chrome.tabs.query({ active: true, currentWindow: true });
  const tab = tabs[0];
  if (!tab || typeof tab.id !== 'number') {
    throw new Error('active tab が取得できませんでした');
  }
  return tab;
}

async function attachSelection(tabId?: number): Promise<Attachment> {
  const tab = await getActiveTab(tabId);
  if (typeof tab.id !== 'number') {
    throw new Error('tabId が不正です');
  }

  const results = await chrome.scripting.executeScript({
    target: { tabId: tab.id },
    func: () => {
      const selected = window.getSelection()?.toString() ?? '';
      return {
        text: selected.trim(),
        url: window.location.href
      };
    }
  });

  const first = results[0]?.result as { text: string; url: string } | undefined;
  if (!first || !first.text) {
    throw new Error('選択テキストが空です');
  }

  return {
    type: 'selected_text',
    text: first.text,
    tabId: tab.id,
    url: first.url,
    capturedAt: now()
  };
}

async function handleCommand(command: RuntimeCommand): Promise<unknown> {
  switch (command.type) {
    case 'CONNECT_WS': {
      const settings = await repository.getSettings();
      const url = command.payload.url?.trim() || settings.wsUrl || DEFAULT_WS_URL;
      transport.connect(url);
      return { status: wsStatus, reason: wsReason };
    }
    case 'DISCONNECT_WS': {
      transport.disconnect();
      return { status: wsStatus, reason: wsReason };
    }
    case 'SEND_CHAT_MESSAGE': {
      const thread = await ensureThread(command.payload.threadId);
      const userMessage: Message = {
        id: createId('msg'),
        threadId: thread.id,
        role: 'user',
        contentMd: command.payload.text,
        attachments: command.payload.attachments,
        createdAt: now(),
        status: 'done'
      };

      const assistantMessage: Message = {
        id: createId('msg'),
        threadId: thread.id,
        role: 'assistant',
        contentMd: '',
        createdAt: now(),
        status: 'streaming'
      };

      await repository.appendMessage(userMessage);
      await repository.appendMessage(assistantMessage);
      await repository.setCurrentThread(thread.id);

      try {
        transport.sendChat({
          threadId: thread.id,
          messageId: assistantMessage.id,
          text: command.payload.text,
          attachments: command.payload.attachments
        });
      } catch (error) {
        const message = error instanceof Error ? error.message : String(error);
        await repository.updateMessage(thread.id, assistantMessage.id, { status: 'error' });
        await broadcast({
          type: 'CHAT_ERROR',
          payload: {
            threadId: thread.id,
            messageId: assistantMessage.id,
            error: message
          }
        });
      }

      return {
        userMessage,
        assistantMessage
      };
    }
    case 'ATTACH_SELECTION': {
      const attachment = await attachSelection(command.payload.tabId);
      await broadcast({
        type: 'SELECTION_ATTACHED',
        payload: { attachment }
      });
      const result: AttachSelectionResult = { attachment };
      return result;
    }
    case 'CREATE_THREAD': {
      const createdAt = now();
      const thread: Thread = {
        id: createId('thread'),
        title: command.payload.title?.trim() || makeThreadTitle(),
        createdAt,
        updatedAt: createdAt,
        lastMessageAt: createdAt
      };
      await repository.upsertThread(thread);
      await repository.setCurrentThread(thread.id);
      const result: CreateThreadResult = { thread };
      return result;
    }
    case 'SWITCH_THREAD': {
      await repository.setCurrentThread(command.payload.threadId);
      return { threadId: command.payload.threadId };
    }
    case 'DELETE_THREAD': {
      await repository.deleteThread(command.payload.threadId);
      return { threadId: command.payload.threadId };
    }
    case 'LIST_THREADS': {
      const threads = await repository.listThreads();
      const currentThreadId = await repository.getCurrentThreadId();
      const result: ListThreadsResult = { threads, currentThreadId };
      return result;
    }
