    12	
    13	  html = html.replace(/```([\s\S]*?)```/g, (_match, code) => {
    14	    return `<pre><code>${code.trim()}</code></pre>`;
    15	  });
    16	
    17	  html = html.replace(/`([^`]+)`/g, '<code>$1</code>');
    18	  html = html.replace(/\*\*([^*]+)\*\*/g, '<strong>$1</strong>');
    19	  html = html.replace(/\*([^*]+)\*/g, '<em>$1</em>');
    20	  html = html.replace(/^### (.*)$/gm, '<h3>$1</h3>');
    21	  html = html.replace(/^## (.*)$/gm, '<h2>$1</h2>');
    22	  html = html.replace(/^# (.*)$/gm, '<h1>$1</h1>');
    23	  html = html.replace(/\n/g, '<br/>');
    24	
    25	  return html;
    26	}

