    14	  const keptIds = new Set(kept.map((thread) => thread.id));
    15	
    16	  const removedThreadIds = sorted
    17	    .slice(maxThreads)
    18	    .map((thread) => thread.id);
    19	
    20	  const nextMessages: Record<string, Message[]> = {};
    21	  for (const [threadId, messages] of Object.entries(messagesByThread)) {
    22	    if (keptIds.has(threadId)) {
    23	      nextMessages[threadId] = messages;
    24	    }
    25	  }
    26	
