let wsStatus: WsStatus = 'disconnected';
let wsReason: string | undefined;
const messageUpdateQueue = new Map<string, Promise<void>>();

function now(): number {
  return Date.now();
}

function makeThreadTitle(): string {
  return `スレッド ${new Date().toLocaleString('ja-JP')}`;
}

async function broadcast(event: SidePanelEvent): Promise<void> {
  const disconnectedPorts: chrome.runtime.Port[] = [];

  for (const port of sidePanelPorts) {
    try {
      port.postMessage(event);
    } catch {
      disconnectedPorts.push(port);
    }
  }

  for (const port of disconnectedPorts) {
    sidePanelPorts.delete(port);
  }
}

function isSidePanelPort(port: chrome.runtime.Port): boolean {
  if (port.name !== SIDEPANEL_EVENT_PORT_NAME) {
    return false;
  }

  const senderUrl = port.sender?.url;
  return typeof senderUrl === 'string' && senderUrl.includes('/sidepanel.html');
}

async function setStatus(status: WsStatus, reason?: string): Promise<void> {
  wsStatus = status;
  wsReason = reason;
  await broadcast({
    type: 'WS_STATUS_CHANGED',
    payload: { status, reason }
  });
}

function messageQueueKey(threadId: string, messageId: string): string {
  return `${threadId}:${messageId}`;
}

function enqueueMessageUpdate(
  threadId: string,
  messageId: string,
  task: () => Promise<void>
): Promise<void> {
  const key = messageQueueKey(threadId, messageId);
  const prev = messageUpdateQueue.get(key) ?? Promise.resolve();
  const next = prev
    .catch(() => {
      // previous task error は後続更新を止めない
    })
    .then(task);

  messageUpdateQueue.set(key, next);
  return next.finally(() => {
    if (messageUpdateQueue.get(key) === next) {
      messageUpdateQueue.delete(key);
    }
  });
}

async function findFallbackAssistantMessageId(threadId: string): Promise<string | undefined> {
  const messages = await repository.getThreadMessages(threadId);

  for (let i = messages.length - 1; i >= 0; i -= 1) {
    const message = messages[i];
    if (message.role === 'assistant' && message.status === 'streaming') {
      return message.id;
    }
  }

  for (let i = messages.length - 1; i >= 0; i -= 1) {
    const message = messages[i];
    if (message.role === 'assistant') {
      return message.id;
    }
  }

  return undefined;
}

async function appendTokenWithFallback(
  threadId: string,
  messageId: string | undefined,
  token: string
): Promise<{ resolvedMessageId?: string; updated?: Message }> {
  if (messageId) {
    let updated: Message | undefined;
    await enqueueMessageUpdate(threadId, messageId, async () => {
      updated = await repository.appendToken(threadId, messageId, token);
    });
    if (updated) {
      return { resolvedMessageId: messageId, updated };
    }
