     6	  return JSON.parse(JSON.stringify(value)) as T;
     7	}
     8	
     9	function createChromeStorageMock(
    10	  initial?: StorageShape,
    11	  delays?: { getMs?: number; setMs?: number }
    12	): chrome.storage.LocalStorageArea {
    13	  let store = initial
    14	    ? { codex_extension_store_v1: initial }
    15	    : {};
    16	
    17	  const wait = async (ms: number | undefined): Promise<void> => {
    18	    if (!ms || ms <= 0) {
    19	      return;
    20	    }
    21	    await new Promise((resolve) => {
    22	      setTimeout(resolve, ms);
    23	    });
    24	  };
    25	
    26	  return {
    27	    get: vi.fn(async (_keys?: string | string[] | Record<string, unknown> | null) => {
    28	      await wait(delays?.getMs);
    29	      return cloneStore(store);
    30	    }),
    31	    set: vi.fn(async (items: Record<string, unknown>) => {
    32	      await wait(delays?.setMs);
    33	      store = { ...store, ...items };
    34	    }),
    35	    remove: vi.fn(async (_keys: string | string[]) => {}),
    36	    clear: vi.fn(async () => {
    37	      store = {};
    38	    }),
    39	    getBytesInUse: vi.fn(async (_keys?: string | string[] | null) => 0)
    40	  } as unknown as chrome.storage.LocalStorageArea;
    41	}
    42	
