    transport.reconnectNow('ws://localhost:4000');
    expect(FakeWebSocket.instances.length).toBe(2);
    expect(FakeWebSocket.instances[1].url).toBe('ws://localhost:4000');
  });

  it('item/agentMessage/delta を token として解釈する', () => {
    const onToken = vi.fn();
    const transport = new WebSocketTransport({
      onStatus: () => {},
      onDebug: () => {},
      onToken,
      onDone: () => {},
      onError: () => {}
    });

    transport.connect('ws://localhost:3000');
    const socket = FakeWebSocket.instances[0];
    openAndInitialize(socket);

    socket.onmessage?.({
      data: JSON.stringify({
        method: 'item/agentMessage/delta',
        params: {
          threadId: 't1',
          turnId: 'turn-1',
          itemId: 'item-1',
          delta: 'こんにちは'
        }
      })
    } as MessageEvent);

    expect(onToken).toHaveBeenCalledWith({
      threadId: 't1',
      messageId: 'item-1',
      token: 'こんにちは'
    });
  });

  it('turn/completed で messageId 欠落時は送信済み local messageId を補完する', () => {
    const onDone = vi.fn();
    const transport = new WebSocketTransport({
      onStatus: () => {},
      onDebug: () => {},
      onToken: () => {},
      onDone,
      onError: () => {}
    });

    transport.connect('ws://localhost:3000');
    const socket = FakeWebSocket.instances[0];
    openAndInitialize(socket);

    transport.sendChat({
      threadId: 't1',
      messageId: 'local-msg-1',
      text: 'hi',
      attachments: []
    });

    socket.onmessage?.({
      data: JSON.stringify({
        method: 'turn/completed',
        params: {
          threadId: 't1',
          turn: { id: 'turn-1', status: 'completed', error: null }
        }
      })
    } as MessageEvent);

    expect(onDone).toHaveBeenCalledWith({
      threadId: 't1',
      messageId: 'local-msg-1'
    });
  });

  it('JSON-RPC error response を onError に流す', () => {
    const onError = vi.fn();
    const transport = new WebSocketTransport({
      onStatus: () => {},
      onDebug: () => {},
      onToken: () => {},
      onDone: () => {},
      onError
    });

    transport.connect('ws://localhost:3000');
    const socket = FakeWebSocket.instances[0];
    openAndInitialize(socket);

    socket.onmessage?.({
      data: JSON.stringify({
        id: '2',
        error: {
          code: -32600,
          message: 'thread not found'
        }
      })
