          payload: { threadId, messageId: resolvedMessageId, token }
        });
      }
    })();
  },
  onDone: ({ threadId, messageId }) => {
    void (async () => {
      const resolvedMessageId = await updateMessageStatusWithFallback(threadId, messageId, 'done');
