    'DISCONNECT_WS',
    'SEND_CHAT_MESSAGE',
    'ATTACH_SELECTION',
    'CREATE_THREAD',
    'SWITCH_THREAD',
    'DELETE_THREAD',
    'LIST_THREADS',
    'GET_THREAD_MESSAGES',
    'SAVE_SETTINGS',
    'GET_SETTINGS'
  ].includes(type);
}

