function isRuntimeCommand(message: unknown): message is RuntimeCommand {
  if (!message || typeof message !== 'object' || !('type' in message)) {
    return false;
  }

  const type = (message as { type: string }).type;
  return [
    'CONNECT_WS',
    'DISCONNECT_WS',
    'GET_WS_STATUS',
    'SEND_CHAT_MESSAGE',
    'ATTACH_SELECTION',
    'CREATE_THREAD',
    'SWITCH_THREAD',
    'DELETE_THREAD',
    'LIST_THREADS',
    'GET_THREAD_MESSAGES',
    'SAVE_SETTINGS',
    'GET_SETTINGS'
  ].includes(type);
}

