function ThreadList(props: {
  threads: Thread[];
  currentThreadId?: string;
  onSwitch: (threadId: string) => void;
  onDelete: (threadId: string) => void;
  onRename: (threadId: string, title: string) => Promise<void>;
}): JSX.Element {
  const [editingThreadId, setEditingThreadId] = useState<string>();
  const [editingTitle, setEditingTitle] = useState('');

  useEffect(() => {
    if (!editingThreadId) {
      return;
    }
    const exists = props.threads.some((thread) => thread.id === editingThreadId);
    if (!exists) {
      setEditingThreadId(undefined);
      setEditingTitle('');
    }
  }, [editingThreadId, props.threads]);

  function startRename(thread: Thread): void {
    setEditingThreadId(thread.id);
    setEditingTitle(thread.title);
  }

  function cancelRename(): void {
    setEditingThreadId(undefined);
    setEditingTitle('');
  }

  async function submitRename(threadId: string): Promise<void> {
    const nextTitle = editingTitle.trim();
    if (!nextTitle) {
      return;
    }
    await props.onRename(threadId, nextTitle);
    cancelRename();
  }

  return (
    <div className="threads">
      {props.threads.map((thread) => (
        <div key={thread.id} className="thread-row">
          {editingThreadId === thread.id ? (
            <form
              className="thread-rename-form"
              onSubmit={(event) => {
                event.preventDefault();
                void submitRename(thread.id);
              }}
            >
              <input value={editingTitle} onChange={(event) => setEditingTitle(event.target.value)} autoFocus />
              <div className="thread-actions">
                <button type="submit" disabled={!editingTitle.trim()}>
                  保存
                </button>
                <button type="button" onClick={() => cancelRename()}>
                  取消
                </button>
              </div>
            </form>
          ) : (
            <>
              <button
