async function getDomSelectionState(tabId?: number): Promise<DomSelectionState> {
  const tab = await getActiveTab(tabId);
  if (typeof tab.id !== 'number') {
    throw new Error('tabId が不正です');
  }
  await ensureTabHostPermission(tab);
