    return false;
  }

  void (async () => {
    try {
      const data = await handleCommand(message);
      const response: ApiResponse<unknown> = { ok: true, data };
      sendResponse(response);
    } catch (error) {
      const response: ApiResponse<unknown> = {
        ok: false,
        error: error instanceof Error ? error.message : String(error)
      };
      sendResponse(response);
    }
  })();

  return true;
});

