function extractThreadNumber(title: string): number | undefined {
  const matched = title.match(/^Thread #(\d+)$/);
  if (!matched) {
    return undefined;
  }
  const num = Number.parseInt(matched[1], 10);
  if (!Number.isFinite(num) || num <= 0) {
    return undefined;
