async function refreshUsageLimits(timeoutMs = 1600): Promise<UsageLimits> {
  const before = usageLimits.updatedAt;
  transport.requestUsageLimits();
  const startedAt = now();
  while (now() - startedAt < timeoutMs) {
    if (usageLimits.updatedAt > before) {
